(Bot.prototype as any).on = function (event: string, ...args: any[]): any {
  if (args.length === 1) {
    // on(event, handler)
    const handler = args[0];
    this.register(event, async (raw: any) => {
      await handler(raw);
    });
    return this;
  } else if (args.length === 2) {
    // on(event, filter, handler)
    const [filter, handler] = args;

    if (typeof filter === 'string') {
      this.register(event, {
        filter: (raw: any) => valueExists(raw, filter),
        handler: async (raw: any) => {
          await handler(raw);
        },
      });
    } else if (typeof filter === 'function') {
      this.register(event, async (raw: any) => {
        if (filter(raw)) {
          await handler(raw);
        }
      });
    }
    return this;
  }

  throw new Error('Invalid arguments for on() method');
};

/**
 * Registers a handler for Update updates with optional filtering
 * @memberof Bot.prototype
 * @instance
 * @function onUpdate
 * @param {string | ((data: Update) => boolean) | ((data: Update) => void | Promise<void>)} filterOrHandler - Filter or handler function
 * @param {(data: Update) => void | Promise<void>} [handler] - Async handler function (if first param is filter)
 * @returns {Bot} Bot instance for chaining
 * @see {@link https://core.telegram.org/bots/api#update Telegram Bot API}
 * @example
 * // Without filter
 * bot.onUpdate(async (data) => { ... });
 *
 * // With string filter (value exists in raw data)
 * bot.onUpdate("start", async (data) => { ... });
 *
 * // With function filter (works with typed object)
 * bot.onUpdate((obj) => obj.someProperty === "value", async (obj) => { ... });
 */
(Bot.prototype as any).onUpdate = function (
  filterOrHandler: string | ((data: Update) => boolean) | ((data: Update) => void | Promise<void>),
  handler?: (data: Update) => void | Promise<void>
) {
  if (typeof filterOrHandler === 'function' && handler === undefined) {
    const handlerFunc = filterOrHandler as (data: Update) => void | Promise<void>;
    this.register('update', async (raw: any) => {
      const data = new Update(raw, this);
      await handlerFunc(data);
    });
    return this;
  }

  const filterParam = filterOrHandler as string | ((data: Update) => boolean);
  const handlerFunc = handler!;

  if (typeof filterParam === 'string') {
    this.register('update', {
      filter: (raw: any) => valueExists(raw, filterParam),
      handler: async (raw: any) => {
        const data = new Update(raw, this);
        await handlerFunc(data);
      },
    });
  } else {
    this.register('update', async (raw: any) => {
      const data = new Update(raw, this);
      if (filterParam(data)) {
        await handlerFunc(data);
      }
    });
  }

  return this;
};

